0],1,"markdown"],[0,[],0,", "],[0,[0],1,"html, embeds "],[0,[],0,"and more."]]],[1,"p",[[0,[0],1,"For example"],[0,[],0,":"]]],[3,"ul",[[[0,[],0,"Insert a video from YouTube directly by pasting the URL"]],[[0,[],0,"Create unique content like buttons or forms using the HTML card"]],[[0,[],0,"Need to share some code? Embed code blocks directly "]]]],[10,1],[1,"p",[[0,[],0,"It's also possible to share links from across the web in a visual way using bookmark cards that automatically render information from a websites meta data. Paste any URL to try it out: "]]],[10,2],[1,"h2",[[0,[],0,"Working with images in posts"]]],[1,"p",[[0,[],0,"You can add images to your posts in many ways:"]]],[3,"ul",[[[0,[],0,"Upload from your computer"]],[[0,[],0,"Click and drag an image into the browser"]],[[0,[],0,"Paste directly into the editor from your clipboard"]],[[0,[],0,"Insert using a URL"]]]],[1,"h3",[[0,[],0,"Image sizes"]]],[1,"p",[[0,[],0,"Once inserted you can blend images beautifully into your content at different sizes and add captions and alt tags wherever needed."]]],[10,3],[1,"h3",[[0,[],0,"Image galleries"]]],[1,"p",[[0,[],0,"Tell visual stories using the gallery card to add up to 9 images that will display as a responsive image gallery: "]]],[10,4],[1,"h3",[[0,[],0,"Image optimisation"]]],[1,"p",[[0,[],0,"Ghost will automatically resize and optimise your images with lossless compression. Your posts will be fully optimised for the web without any extra effort on your part."]]],[1,"h2",[[0,[],0,"Next: Publishing Options"]]],[1,"p",[[0,[],0,"Once your post is looking good, you'll want to use the "],[0,[2],1,"publishing options"],[0,[],0," to ensure it gets distributed in the right places, with custom meta data, feature images and more."]]],[1,"p",[]]],"ghostVersion":"3.0"}

